// Background processing function
async function processDocument(documentId: string, filePath: string) {
  try {
    await storage.updateDocumentStatus(documentId, "processing");

    // Extract text from PDF
    const textContent = await pdfService.extractText(filePath);
    await storage.updateDocumentContent(documentId, textContent);

    // Extract nodes and relations using Langfuse
    const extraction = await langfuseService.extractNodesAndRelations(textContent, documentId);

    // Get existing categories
    const categories = await storage.getCategories();
    const categoryIds = new Set(categories.map(c => c.id));

    // Store extracted nodes
    for (const nodeData of extraction.nodes) {
      // Keep the original AI-extracted type
      // The AI already returns valid types like: person, equipment, process, concept, material, etc.
      const nodeType = nodeData.type.toLowerCase();

      // Ensure the category exists (create if needed)
      if (!categoryIds.has(nodeType) && nodeType !== "unknown") {
        // Create the category if it doesn't exist
        const categoryColors: Record<string, string> = {
          person: "#FA4D56",
          equipment: "#8A3FFC",
          process: "#F1C21B",
          concept: "#24A148",
          material: "#BA4E00",
          organization: "#A2191F",
          location: "#007D79",
          technology: "#198038",
          method: "#6929C4",
          tool: "#8A3FFC"
        };

        // Create category with ID matching the type
        const newCategory = {
          id: nodeType,
          name: nodeType.charAt(0).toUpperCase() + nodeType.slice(1),
          color: categoryColors[nodeType] || "#0F62FE",
          description: `AI-detected ${nodeType} entities`
        };
        await storage.createCategoryWithId(newCategory);
        categoryIds.add(nodeType);
      }

      await storage.createGraphNode({
        name: nodeData.name,
        description: nodeData.description,
        type: nodeType,
        confidence: nodeData.confidence.toString(),
        sourceDocumentId: documentId,
        status: "pending",
      });
    }

    // Store extracted relations
    for (const relationData of extraction.relations) {
      // Find the actual node IDs (simplified - in reality would need better matching)
      const fromNodeCandidates = await storage.getGraphNodesByStatus("pending");
      const toNodeCandidates = await storage.getGraphNodesByStatus("pending");

      const fromNode = fromNodeCandidates.find(n => n.name === relationData.fromNode);
      const toNode = toNodeCandidates.find(n => n.name === relationData.toNode);

      if (fromNode && toNode) {
        await storage.createGraphRelation({
          fromNodeId: fromNode.id,
          toNodeId: toNode.id,
          relationshipType: relationData.relationshipType,
          description: relationData.description,
          confidence: relationData.confidence.toString(),
          sourceDocumentId: documentId,
          status: "pending",
        });
      }
    }

    await storage.updateDocumentStatus(documentId, "processed");
  } catch (error) {
    console.error("Error processing document:", error);
    await storage.updateDocumentStatus(
      documentId,
      "failed",
      error instanceof Error ? error.message : "Unknown error"
    );
  }
}

// Helper functions for formatting query results
function formatQueryResults(results: any[]): string {
  if (results.length === 0) {
    return "I couldn't find any results for your query.";
  }

  // Check if this is an ingredient query result (check this BEFORE template entities)
  if (results[0] && results[0].entity && results[0].ingredients) {
    return formatIngredientResults(results);
  }

  // Check if this is a recipe search result
  if (results[0] && results[0].searchedIngredient && results[0].recipes) {
    return formatRecipeSearchResults(results);
  }

  // Check if this is a template query result with entities array or recipes/ingredients
  if (results[0] && (results[0].entities || (results[0].recipes && !results[0].searchedIngredient) || (results[0].ingredients && !results[0].entity)) && 
      (Array.isArray(results[0].entities) || Array.isArray(results[0].recipes) || Array.isArray(results[0].ingredients))) {
    return formatTemplateEntityResults(results);
  }

  // Check if this is a relationship path result
  if (results[0] && (results[0].relationshipPath || results[0].pathLength)) {
    return formatRelationshipPathResults(results);
  }

  // Check if this is the node overview query with typeCount and examples
  if (results[0] && results[0].typeCount !== undefined && results[0].examples !== undefined) {
    return formatNodeOverviewResults(results);
  }

  // Check if this is a count/aggregate query
  if (results[0] && Object.keys(results[0]).some(key => 
    key.includes('count') || key.includes('Count') || key.includes('COUNT'))) {
    return formatAggregateResults(results);
  }

  // Check if this is asking about node types/labels
  if (results[0] && (results[0].nodeLabels || results[0].nodeLabel || results[0].labels)) {
    return formatNodeTypeResults(results);
  }

  // Check if this is asking about specific nodes
  if (results[0] && (results[0].name || results[0].n)) {
    return formatNodeResults(results);
  }

  // Check if this is asking about relationships
  if (results[0] && (results[0].relationshipType || results[0].type || results[0].r)) {
    return formatRelationshipResults(results);
  }

  // Default formatting for other queries
  return formatDefaultResults(results);
}

function formatTemplateEntityResults(results: any[]): string {
  // Handle template query results with entities array
  let allEntities: any[] = [];
  let entityType = '';

  // Collect all entities from all result types
  results.forEach(result => {
    const type = result.type;
    const entities = result.entities || result.recipes || result.ingredients;

    if (entities && entities.length > 0) {
      allEntities = allEntities.concat(entities);
      // Set entity type based on the query type or what field the data is in
      if (!entityType) {
        if (result.recipes) {
          entityType = 'recipe';
        } else if (result.ingredients) {
          entityType = 'ingredient';
        } else if (type === 'dish' || type === 'recipe' || type === 'meal') {
          entityType = 'recipe';
        } else if (type === 'ingredient') {
          entityType = 'ingredient';
        }
      }
    }
  });

  if (allEntities.length === 0) {
    return "I don't have any entities matching your query in my knowledge graph.";
  }

  // Format the combined results - be accurate about what we're returning
  let typeLabel = '';
  if (entityType === 'recipe') {
    // These are actually recipes AND dishes, so be accurate
    typeLabel = allEntities.length === 1 ? 'recipe/dish' : 'recipes and dishes';
  } else if (entityType === 'ingredient') {
    typeLabel = allEntities.length === 1 ? 'ingredient' : 'ingredients';
  } else {
    typeLabel = allEntities.length === 1 ? 'result' : 'results';
  }

  let response = `Here ${allEntities.length === 1 ? 'is' : 'are'} the ${allEntities.length} ${typeLabel} I have in my knowledge graph:\n\n`;

  allEntities.forEach((entity: any, index: number) => {
    if (index >= 15) return; // Limit to first 15 items
    response += `â€¢ ${entity.name}`;
    if (entity.description) {
      response += ` - ${entity.description}`;
    }
    response += '\n';
  });

  if (allEntities.length > 15) {
    response += `\n... and ${allEntities.length - 15} more`;
  }

  return response.trim();
}

function formatRelationshipPathResults(results: any[]): string {
  if (results.length === 0) {
    return "No relationship found between these entities.";
  }

  const firstResult = results[0];
  const source = firstResult.source;
  const target = firstResult.target;
  const relationshipPath = firstResult.relationshipPath;
  const pathLength = firstResult.pathLength;

  let response = `Found ${results.length} relationship${results.length > 1 ? 's' : ''} between "${source}" and "${target}":\n\n`;

  results.slice(0, 3).forEach((result, index) => {
    const path = result.relationshipPath;
    response += `${index + 1}. `;
    if (path && path.length > 0) {
      response += path.join(' â†’ ');
    } else {
      response += 'Direct connection';
    }
    response += ` (${result.pathLength || 1} step${result.pathLength !== 1 ? 's' : ''})\n`;
  });

  if (results.length > 3) {
    response += `\n... and ${results.length - 3} more relationships`;
  }

  return response.trim();
}

function formatNodeTypeResults(results: any[]): string {
  const nodeTypes = new Map<string, number>();

  results.forEach(result => {
    const label = result.nodeLabels || result.nodeLabel || result.labels || result.type;
    const count = result.nodeCount || result.count || 1;
    nodeTypes.set(label, count);
  });

  if (nodeTypes.size === 0) {
    return "I don't have any nodes in the knowledge graph yet.";
  }

  let response = `I have ${nodeTypes.size} type${nodeTypes.size > 1 ? 's' : ''} of nodes in the knowledge graph:\n\n`;

  Array.from(nodeTypes.entries()).forEach(([type, count]) => {
    const typeName = type.charAt(0).toUpperCase() + type.slice(1);
    response += `â€¢ ${count} ${typeName}${count > 1 ? ' nodes' : ' node'}\n`;
  });

  return response.trim();
}

function formatNodeResults(results: any[]): string {
  const nodes = results.slice(0, 10);

  if (nodes.length === 1) {
    const result = nodes[0];
    // Check if 'n' exists as a Neo4j node object
    const node = result.n ? (result.n.properties || result.n) : result;
    const name = node.name || 'Unknown';
    const description = node.description || 'No description available';
    const type = node.type || 'Unknown';
    return `I found this node:\n\n**${name}**\n${description}\nType: ${type}`;
  }

  let response = `I found ${results.length} node${results.length > 1 ? 's' : ''}:\n\n`;

  nodes.forEach((result, index) => {
    // Check if 'n' exists as a Neo4j node object
    const node = result.n ? (result.n.properties || result.n) : result;
    const name = node.name || 'Unknown';
    const type = node.type;
    const description = node.description;

    response += `${index + 1}. **${name}**`;
    if (type) response += ` (${type})`;
    if (description) response += `\n   ${description}`;
    response += '\n\n';
  });

  if (results.length > 10) {
    response += `... and ${results.length - 10} more nodes.`;
  }

  return response.trim();
}

function formatRelationshipResults(results: any[]): string {
  const relationships = results.slice(0, 10);

  if (results.length === 0) {
    return "I couldn't find any relationships matching your query.";
  }

  let response = `I found ${results.length} relationship${results.length > 1 ? 's' : ''} in the knowledge graph:\n\n`;

  relationships.forEach((result, index) => {
    const rel = result.r || result;
    const fromNode = result.fromNode || result.source || 'Unknown';
    const toNode = result.toNode || result.target || 'Unknown';
    const relType = rel.relationshipType || rel.type || 'RELATED_TO';

    response += `${index + 1}. ${fromNode} â†’ ${relType} â†’ ${toNode}`;
    if (rel.description) response += `\n   ${rel.description}`;
    response += '\n\n';
  });

  if (results.length > 10) {
    response += `... and ${results.length - 10} more relationships.`;
  }

  return response.trim();
}

function formatAggregateResults(results: any[]): string {
    const result = results[0];
    const entries = Object.entries(result);

    if (entries.length === 1) {
      const [key, value] = entries[0];
      const cleanKey = key.replace(/count|Count|COUNT/gi, '').trim();
      // Handle Neo4j integer format
      const displayValue = typeof value === 'object' && value.low !== undefined ? value.low : value;
      return `The total ${cleanKey || 'count'} is ${displayValue}.`;
    }

    let response = "Here are the results:\n\n";
    entries.forEach(([key, value]) => {
      const cleanKey = key.replace(/_/g, ' ').toLowerCase();
      // Handle Neo4j integer format
      const displayValue = typeof value === 'object' && value.low !== undefined ? value.low : value;
      response += `â€¢ ${cleanKey}: ${displayValue}\n`;
    });

    return response.trim();
}

function formatNodeOverviewResults(results: any[]): string {
    if (results.length === 0) {
      return "I don't have any nodes in the knowledge graph yet.";
    }

    // Calculate total nodes
    let totalNodes = 0;
    results.forEach(result => {
      const count = typeof result.typeCount === 'object' && result.typeCount.low !== undefined 
        ? result.typeCount.low 
        : result.typeCount;
      totalNodes += count;
    });

    let response = `I have **${totalNodes} nodes** in the knowledge graph across ${results.length} different types:\n\n`;

    results.forEach(result => {
      const type = result.type || 'unknown';
      const count = typeof result.typeCount === 'object' && result.typeCount.low !== undefined 
        ? result.typeCount.low 
        : result.typeCount;
      const examples = result.examples || [];

      // Format type name
      const typeName = type.charAt(0).toUpperCase() + type.slice(1);
      response += `**${typeName}s** (${count} nodes)\n`;

      // Add examples if available
      if (examples.length > 0) {
        examples.slice(0, 3).forEach((example: any) => {
          response += `  â€¢ ${example.name}`;
          if (example.description) {
            response += `: ${example.description}`;
          }
          response += '\n';
        });
        if (count > examples.length) {
          response += `  ... and ${count - examples.length} more\n`;
        }
      }
      response += '\n';
    });

    return response.trim();
}

function formatRecipeSearchResults(results: any[]): string {
  if (results.length === 0) {
    return "I couldn't find any recipes with that ingredient.";
  }

  let response = '';

  results.forEach((result) => {
    const ingredient = result.searchedIngredient;
    const recipes = result.recipes || [];

    if (recipes.length === 0) {
      response += `I don't have any recipes that use ${ingredient} in my knowledge graph.\n`;
    } else {
      const recipeCount = recipes.length;
      response += `I found ${recipeCount} ${recipeCount === 1 ? 'recipe' : 'recipes'} that ${recipeCount === 1 ? 'uses' : 'use'} ${ingredient}:\n\n`;

      recipes.forEach((recipe: any, index: number) => {
        if (typeof recipe === 'string') {
          response += `â€¢ ${recipe}\n`;
        } else if (recipe && recipe.name) {
          response += `â€¢ ${recipe.name}`;
          if (recipe.description) {
            response += ` - ${recipe.description}`;
          }
          response += '\n';
        }
      });
    }
  });

  return response.trim();
}

function formatIngredientResults(results: any[]): string {
  if (results.length === 0) {
    return "I couldn't find that dish in my knowledge graph.";
  }

  let response = '';

  results.forEach((result) => {
    const dishName = result.entity;
    const ingredients = result.ingredients || [];

    if (ingredients.length === 0) {
      response += `${dishName} doesn't have any ingredients listed.\n`;
    } else {
      response += `${dishName} contains the following ingredients:\n\n`;

      const ingredientList = ingredients.map((ing: any) => {
        if (typeof ing === 'string') {
          return `â€¢ ${ing}`;
        } else if (ing && ing.name) {
          let item = `â€¢ ${ing.name}`;
          if (ing.description) {
            item += ` - ${ing.description}`;
          }
          return item;
        }
        return 'â€¢ Unknown ingredient';
      });

      response += ingredientList.join('\n') + '\n';
    }
  });

  return response.trim();
}

function formatDefaultResults(results: any[]): string {
    if (results.length === 0) {
      return "No results found.";
    }

    // Try to format as a simple list
    let response = `Found ${results.length} result${results.length > 1 ? 's' : ''}:\n\n`;

    results.slice(0, 10).forEach((result, index) => {
      // Try to extract meaningful information from the result
      const keys = Object.keys(result);
      if (keys.length === 1) {
        response += `${index + 1}. ${result[keys[0]]}\n`;
      } else {
        // Format as key-value pairs
        response += `${index + 1}. `;
        const displayPairs: string[] = [];
        keys.forEach(key => {
          const value = result[key];
          if (value !== null && value !== undefined) {
            // Handle Neo4j integer format
            const displayValue = typeof value === 'object' && value.low !== undefined ? value.low : value;
            displayPairs.push(`${key}: ${displayValue}`);
          }
        });
        response += displayPairs.join(', ') + '\n';
      }
    });

    if (results.length > 10) {
      response += `\n... and ${results.length - 10} more results.`;
    }

    return response.trim();
}

server/storage.ts.content:
import {
  users,
  documents,
  graphNodes,
  graphRelations,
  duplicateCandidates,
  queryTranslations,
  chatSessions,
  chatMessages,
  categories,
  type User,
  type InsertUser,
  type Document,
  type InsertDocument,
  type GraphNode,
  type InsertGraphNode,
  type GraphRelation,
  type InsertGraphRelation,
  type DuplicateCandidate,
  type InsertDuplicateCandidate,
  type QueryTranslation,
  type InsertQueryTranslation,
  type ChatSession,
  type InsertChatSession,
  type ChatMessage,
  type InsertChatMessage,
  type Category,
  type InsertCategory,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, or, like, gte, lte, inArray, sql } from "drizzle-orm";


export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Categories
  getCategories(): Promise<Category[]>;
  createCategory(category: { name: string; color: string; description?: string }): Promise<Category>;
  updateCategory(id: string, updates: Partial<Category>): Promise<Category>;
  deleteCategory(id: string): Promise<void>;
  updateNodeCategory(nodeId: string, categoryId: string): Promise<GraphNode>;

  // Documents
  createDocument(document: InsertDocument): Promise<Document>;
  getDocument(id: string): Promise<Document | undefined>;
  getDocuments(): Promise<Document[]>;
  updateDocumentStatus(id: string, status: string, errorMessage?: string): Promise<void>;
  updateDocumentContent(id: string, textContent: string): Promise<void>;
  deleteDocument(id: string): Promise<void>;

  // Graph Nodes
  createGraphNode(node: InsertGraphNode): Promise<GraphNode>;
  getGraphNode(id: string): Promise<GraphNode | undefined>;
  getGraphNodesByStatus(status: string): Promise<GraphNode[]>;
  getGraphNodesByDocument(documentId: string): Promise<GraphNode[]>;
  updateGraphNodeStatus(id: string, status: string): Promise<void>;
  deleteGraphNode(id: string): Promise<void>;
  getGraphNodesWithRelations(): Promise<GraphNode[]>;

  // Graph Relations
  createGraphRelation(relation: InsertGraphRelation): Promise<GraphRelation>;
  getGraphRelation(id: string): Promise<GraphRelation | undefined>;
  getGraphRelationsByStatus(status: string): Promise<GraphRelation[]>;
  getGraphRelationsByDocument(documentId: string): Promise<GraphRelation[]>;
  updateGraphRelationStatus(id: string, status: string): Promise<void>;
  deleteGraphRelation(id: string): Promise<void>;

  // Duplicate Candidates
  createDuplicateCandidate(candidate: InsertDuplicateCandidate): Promise<DuplicateCandidate>;
  getDuplicateCandidatesByStatus(status: string): Promise<DuplicateCandidate[]>;
  updateDuplicateCandidateStatus(id: string, status: string): Promise<void>;

  // Query Translations
  createQueryTranslation(translation: InsertQueryTranslation): Promise<QueryTranslation>;
  getQueryTranslations(): Promise<QueryTranslation[]>;
  updateQueryTranslationApproval(id: string, approved: boolean): Promise<void>;

  // Chat
  createChatSession(): Promise<ChatSession>;
  getChatSession(id: string): Promise<ChatSession | undefined>;
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
  getChatMessages(sessionId: string): Promise<ChatMessage[]>;

  // Cascade deletion
  deleteGraphNodesByDocument(documentId: string): Promise<void>;
  deleteGraphRelationsByDocument(documentId: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async createDocument(document: InsertDocument): Promise<Document> {
    const [doc] = await db.insert(documents).values(document).returning();
    return doc;
  }

  async getDocument(id: string): Promise<Document | undefined> {
    const [doc] = await db.select().from(documents).where(eq(documents.id, id));
    return doc || undefined;
  }

  async getDocuments(): Promise<Document[]> {
    return await db.select().from(documents).orderBy(desc(documents.uploadedAt));
  }

  async updateDocumentStatus(id: string, status: string, errorMessage?: string): Promise<void> {
    await db
      .update(documents)
      .set({ status, errorMessage })
      .where(eq(documents.id, id));
  }

  async updateDocumentContent(id: string, textContent: string): Promise<void> {
    await db
      .update(documents)
      .set({ textContent })
      .where(eq(documents.id, id));
  }

  async deleteDocument(id: string): Promise<void> {
    await db.delete(documents).where(eq(documents.id, id));
  }

  async createGraphNode(node: InsertGraphNode): Promise<GraphNode> {
    const [graphNode] = await db.insert(graphNodes).values(node).returning();
    return graphNode;
  }

  async getGraphNode(id: string): Promise<GraphNode | undefined> {
    const [node] = await db.select().from(graphNodes).where(eq(graphNodes.id, id));
    return node || undefined;
  }

  async getGraphNodesByStatus(status: string): Promise<GraphNode[]> {
    return await db
      .select({
        id: graphNodes.id,
        name: graphNodes.name,
        description: graphNodes.description,
        type: graphNodes.type,
        properties: graphNodes.properties,
        sourceDocumentId: graphNodes.sourceDocumentId,
        sourceDocumentName: documents.originalName,
        confidence: graphNodes.confidence,
        status: graphNodes.status,
        createdAt: graphNodes.createdAt,
        approvedAt: graphNodes.approvedAt,
      })
      .from(graphNodes)
      .leftJoin(documents, eq(graphNodes.sourceDocumentId, documents.id))
      .where(eq(graphNodes.status, status))
      .orderBy(desc(graphNodes.createdAt));
  }

  async getGraphNodesByDocument(documentId: string): Promise<GraphNode[]> {
    return await db
      .select()
      .from(graphNodes)
      .where(eq(graphNodes.sourceDocumentId, documentId))
      .orderBy(desc(graphNodes.createdAt));
  }

  async updateGraphNodeStatus(id: string, status: string): Promise<void> {
    await db
      .update(graphNodes)
      .set({ status, approvedAt: status === "approved" ? sql`NOW()` : null })
      .where(eq(graphNodes.id, id));
  }

  async resetGraphNodeType(id: string): Promise<void> {
    // Reset node type to "unknown" when clearing preview
    await db
      .update(graphNodes)
      .set({ type: "unknown" })
      .where(eq(graphNodes.id, id));
  }

  async deleteGraphNode(id: string): Promise<void> {
    await db.delete(graphNodes).where(eq(graphNodes.id, id));
  }

  async resetInGraphNodesToApproved(): Promise<number> {
    const result = await db
      .update(graphNodes)
      .set({ status: "approved" })
      .where(eq(graphNodes.status, "in_graph"));
    return result.count || 0;
  }

  async resetInGraphRelationsToApproved(): Promise<number> {
    const result = await db
      .update(graphRelations)
      .set({ status: "approved" })
      .where(eq(graphRelations.status, "in_graph"));
    return result.count || 0;
  }

  async getGraphNodesWithRelations(): Promise<GraphNode[]> {
    return await db
      .select()
      .from(graphNodes)
      .where(eq(graphNodes.status, "approved"))
      .orderBy(desc(graphNodes.createdAt));
  }

  async createGraphRelation(relation: InsertGraphRelation): Promise<GraphRelation> {
    const [graphRelation] = await db.insert(graphRelations).values(relation).returning();
    return graphRelation;
  }

  async getGraphRelation(id: string): Promise<GraphRelation | undefined> {
    const [relation] = await db.select().from(graphRelations).where(eq(graphRelations.id, id));
    return relation || undefined;
  }

  async getGraphRelationsByStatus(status: string): Promise<any[]> {
    // Use raw SQL to get relations with node names since we need to join the same table twice
    const result = await db.execute(sql`
      SELECT 
        r.id,
        r.from_node_id as "fromNodeId",
        r.to_node_id as "toNodeId", 
        fn.name as "fromNodeName",
        tn.name as "toNodeName",
        r.relationship_type as "relationshipType",
        r.description,
        r.properties,
        r.source_document_id as "sourceDocumentId",
        d.original_name as "sourceDocumentName",
        r.confidence,
        r.status,
        r.created_at as "createdAt",
        r.approved_at as "approvedAt"
      FROM graph_relations r
      LEFT JOIN graph_nodes fn ON r.from_node_id = fn.id
      LEFT JOIN graph_nodes tn ON r.to_node_id = tn.id
      LEFT JOIN documents d ON r.source_document_id = d.id
      WHERE r.status = ${status}
      ORDER BY r.created_at DESC
    `);

    return result.rows;
  }

  async getGraphRelationsByDocument(documentId: string): Promise<GraphRelation[]> {
    return await db
      .select()
      .from(graphRelations)
      .where(eq(graphRelations.sourceDocumentId, documentId))
      .orderBy(desc(graphRelations.createdAt));
  }

  async updateGraphRelationStatus(id: string, status: string): Promise<void> {
    await db
      .update(graphRelations)
      .set({ status, approvedAt: status === "approved" ? sql`NOW()` : null })
      .where(eq(graphRelations.id, id));
  }

  async deleteGraphRelation(id: string): Promise<void> {
    await db.delete(graphRelations).where(eq(graphRelations.id, id));
  }

  async createDuplicateCandidate(candidate: InsertDuplicateCandidate): Promise<DuplicateCandidate> {
    const [duplicateCandidate] = await db.insert(duplicateCandidates).values(candidate).returning();
    return duplicateCandidate;
  }

  async getDuplicateCandidatesByStatus(status: string): Promise<DuplicateCandidate[]> {
    return await db
      .select()
      .from(duplicateCandidates)
      .where(eq(duplicateCandidates.status, status))
      .orderBy(desc(duplicateCandidates.createdAt));
  }

  async updateDuplicateCandidateStatus(id: string, status: string): Promise<void> {
    await db
      .update(duplicateCandidates)
      .set({ status, resolvedAt: sql`NOW()` })
      .where(eq(duplicateCandidates.id, id));
  }

  async getDuplicateCandidateById(id: string): Promise<DuplicateCandidate | undefined> {
    const [candidate] = await db.select().from(duplicateCandidates).where(eq(duplicateCandidates.id, id));
    return candidate || undefined;
  }

  async getDuplicateCandidateByNodePair(nodeId1: string, nodeId2: string): Promise<DuplicateCandidate | undefined> {
    const [candidate] = await db
      .select()
      .from(duplicateCandidates)
      .where(
        or(
          and(eq(duplicateCandidates.nodeId1, nodeId1), eq(duplicateCandidates.nodeId2, nodeId2)),
          and(eq(duplicateCandidates.nodeId1, nodeId2), eq(duplicateCandidates.nodeId2, nodeId1))
        )
      );
    return candidate || undefined;
  }

  async deleteDuplicateCandidate(candidateId: string): Promise<void> {
    await db.delete(duplicateCandidates).where(eq(duplicateCandidates.id, candidateId));
  }

  async redirectNodeRelations(fromNodeId: string, toNodeId: string): Promise<void> {
    // Update outgoing relations (where fromNodeId is the source)
    await db
      .update(graphRelations)
      .set({ fromNodeId: toNodeId })
      .where(eq(graphRelations.fromNodeId, fromNodeId));

    // Update incoming relations (where fromNodeId is the target)
    await db
      .update(graphRelations)
      .set({ toNodeId: toNodeId })
      .where(eq(graphRelations.toNodeId, fromNodeId));
  }

  async createQueryTranslation(translation: InsertQueryTranslation): Promise<QueryTranslation> {
    const [queryTranslation] = await db.insert(queryTranslations).values(translation).returning();
    return queryTranslation;
  }

  async getQueryTranslations(): Promise<QueryTranslation[]> {
    return await db
      .select()
      .from(queryTranslations)
      .orderBy(desc(queryTranslations.createdAt));
  }

  async updateQueryTranslationApproval(id: string, approved: boolean): Promise<void> {
    await db
      .update(queryTranslations)
      .set({ approved })
      .where(eq(queryTranslations.id, id));
  }

  async createChatSession(): Promise<ChatSession> {
    const [session] = await db.insert(chatSessions).values({}).returning();
    return session;
  }

  async getChatSession(id: string): Promise<ChatSession | undefined> {
    const [session] = await db.select().from(chatSessions).where(eq(chatSessions.id, id));
    return session || undefined;
  }

  async createChatMessage(message: InsertChatMessage): Promise<ChatMessage> {
    const [chatMessage] = await db.insert(chatMessages).values(message).returning();
    return chatMessage;
  }

  async getChatMessages(sessionId: string): Promise<ChatMessage[]> {
    return await db
      .select()
      .from(chatMessages)
      .where(eq(chatMessages.sessionId, sessionId))
      .orderBy(chatMessages.timestamp);
  }

  // Category management methods
  async getCategories(): Promise<Category[]> {
    const dbCategories = await db.select().from(categories);

    // If no categories exist, return default ones
    if (dbCategories.length === 0) {
      const defaultCategories: Category[] = [
        { id: "unknown", name: "Unknown", color: "#525252", description: "Uncategorized nodes", createdAt: new Date() },
        { id: "ingredient", name: "Ingredient", color: "#FF6B6B", description: "Food ingredients", createdAt: new Date() },
        { id: "dish", name: "Dish", color: "#4ECDC4", description: "Prepared dishes", createdAt: new Date() },
        { id: "recipe", name: "Recipe", color: "#45B7D1", description: "Cooking recipes", createdAt: new Date() },
        { id: "entity", name: "Entity", color: "#0F62FE", description: "General entities", createdAt: new Date() },
        { id: "concept", name: "Concept", color: "#24A148", description: "Abstract concepts", createdAt: new Date() },
        { id: "process", name: "Process", color: "#F1C21B", description: "Processes and methods", createdAt: new Date() },
      ];

      // Insert default categories into database
      for (const cat of defaultCategories) {
        await db.insert(categories).values(cat).onConflictDoNothing();
      }

      return defaultCategories;
    }

    return dbCategories;
  }

  async createCategory(category: { name: string; color: string; description?: string }): Promise<Category> {
    const newCategory: InsertCategory = {
      id: crypto.randomUUID(),
      name: category.name,
      color: category.color,
      description: category.description,
    };

    const [created] = await db.insert(categories).values(newCategory).returning();
    return created;
  }

  async createCategoryWithId(category: { id: string; name: string; color: string; description?: string }): Promise<Category> {
    const newCategory: InsertCategory = {
      id: category.id,
      name: category.name,
      color: category.color,
      description: category.description,
    };

    // Use onConflictDoNothing to avoid errors if category already exists
    const result = await db.insert(categories).values(newCategory).onConflictDoNothing().returning();

    // If category already exists, fetch and return it
    if (result.length === 0) {
      const [existing] = await db.select().from(categories).where(eq(categories.id, category.id));
      return existing;
    }

    return result[0];
  }

  async updateCategory(id: string, updates: Partial<Category>): Promise<Category> {
    const [updated] = await db
      .update(categories)
      .set(updates)
      .where(eq(categories.id, id))
      .returning();
    return updated;
  }

  async deleteCategory(id: string): Promise<void> {
    // First move all nodes with this category to "unknown"
    await db
      .update(graphNodes)
      .set({ type: "unknown" })
      .where(eq(graphNodes.type, id));

    // Then delete the category
    await db.delete(categories).where(eq(categories.id, id));
  }

  async updateNodeCategory(nodeId: string, categoryId: string): Promise<GraphNode> {
    // First verify the category exists
    const category = await db
      .select()
      .from(categories)
      .where(eq(categories.id, categoryId))
      .limit(1);

    if (category.length === 0) {
      throw new Error(`Category ${categoryId} not found`);
    }

    // Update the node's type field to the category ID (not name)
    // This ensures consistency with how categories are matched in the graph preview
    const [updatedNode] = await db
      .update(graphNodes)
      .set({ type: categoryId })
      .where(eq(graphNodes.id, nodeId))
      .returning();
    return updatedNode;
  }

  // Add methods for cascade deletion
  async deleteGraphNodesByDocument(documentId: string): Promise<void> {
    await db.delete(graphNodes).where(eq(graphNodes.documentId, documentId));
  }

  async deleteGraphRelationsByDocument(documentId: string): Promise<void> {
    await db.delete(graphRelations).where(eq(graphRelations.documentId, documentId));
  }
}

export const storage = new DatabaseStorage();

