import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useToast } from "@/hooks/use-toast";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { apiRequest } from "@/lib/queryClient";
import { Trash2, Send } from "lucide-react";
import ModelSelector from "./ModelSelector";

interface Message {
  id: string;
  role: "user" | "assistant";
  content: string;
  timestamp: string;
}

interface MetricsData {
  totalQueries: number;
  avgResponseTime: number;
  successRate: number;
  cacheHitRate: number;
}

interface MessagesResponse {
  messages: Message[];
}

export default function ChatInterface() {
  const [currentMessage, setCurrentMessage] = useState("");
  const [sessionId, setSessionId] = useState<string | null>(() => {
    // Try to restore session from localStorage
    return localStorage.getItem('chatSessionId');
  });
  const scrollRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Create or get chat session
  const createSessionMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/chat/sessions");
      return response.json();
    },
    onSuccess: (data) => {
      const newSessionId = data.session.id;
      setSessionId(newSessionId);
      // Save session to localStorage
      localStorage.setItem('chatSessionId', newSessionId);
    },
  });

  // Get chat messages
  const { data: messagesResponse } = useQuery<MessagesResponse>({
    queryKey: ["/api/chat/sessions", sessionId, "messages"],
    enabled: !!sessionId,
  });

  const messages = messagesResponse?.messages || [];

  // Get GraphRAG metrics
  const { data: metricsData } = useQuery<MetricsData>({
    queryKey: ["/api/graphrag/metrics"],
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  // Clear chat mutation
  const clearChatMutation = useMutation({
    mutationFn: async () => {
      // Create a new session
      const response = await apiRequest("POST", "/api/chat/sessions");
      return response.json();
    },
    onSuccess: (data) => {
      const newSessionId = data.session.id;
      setSessionId(newSessionId);
      localStorage.setItem('chatSessionId', newSessionId);
      // Clear the messages cache
      queryClient.invalidateQueries({ queryKey: ["/api/chat/sessions"] });
      toast({
        title: "Chat cleared",
        description: "Started a new conversation",
      });
    },
    onError: (error) => {
      toast({
        title: "Failed to clear chat",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      if (!sessionId) throw new Error("No session");
      const response = await apiRequest("POST", `/api/chat/sessions/${sessionId}/messages`, { content });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/chat/sessions", sessionId, "messages"] });
      setCurrentMessage("");
    },
    onError: (error) => {
      toast({
        title: "Message failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Initialize session on mount
  useEffect(() => {
    if (!sessionId) {
      createSessionMutation.mutate();
    }
  }, [sessionId]);

  // Auto scroll to bottom
  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSendMessage = () => {
    if (!currentMessage.trim()) return;
    sendMessageMutation.mutate(currentMessage);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const quickQueries = [
    "Which nodes do you have?",
    "What ingredients are needed for Flammkuchen?",
    "Show me all recipes",
    "List all ingredients",
  ];

  return (
    <div className="max-w-6xl mx-auto">
      <div className="mb-6">
        <h3 className="text-lg font-medium text-gray-900">Natural Language Query Interface</h3>
        <p className="text-carbon-gray-60">Ask questions about your knowledge graph using natural language</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Chat Interface */}
        <div className="lg:col-span-2">
          <Card className="flex flex-col h-[600px]">
            {/* Chat Header */}
            <div className="border-b border-carbon-gray-20 p-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="w-8 h-8 bg-carbon-blue rounded-full flex items-center justify-center">
                    <i className="fas fa-robot text-white text-sm"></i>
                  </div>
                  <div>
                    <h4 className="font-medium text-gray-900">GraphfloorGPT Assistant</h4>
                    <p className="text-sm text-carbon-gray-60">Connected to your knowledge graph</p>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <ModelSelector />
                  <AlertDialog>
                    <AlertDialogTrigger asChild>
                      <Button
                        variant="ghost"
                        size="sm"
                        disabled={messages.length === 0}
                        title="Clear chat history"
                        className="hover:bg-red-50 hover:text-red-600"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </AlertDialogTrigger>
                    <AlertDialogContent>
                      <AlertDialogHeader>
                        <AlertDialogTitle>Clear Chat History</AlertDialogTitle>
                        <AlertDialogDescription>
                          This will start a new conversation and clear all messages. This action cannot be undone.
                        </AlertDialogDescription>
                      </AlertDialogHeader>
                      <AlertDialogFooter>
                        <AlertDialogCancel>Cancel</AlertDialogCancel>
                        <AlertDialogAction onClick={() => clearChatMutation.mutate()}>
                          Clear Chat
                        </AlertDialogAction>
                      </AlertDialogFooter>
                    </AlertDialogContent>
                  </AlertDialog>
                </div>
              </div>
            </div>

            {/* Chat Messages */}
            <ScrollArea className="flex-1 p-4 h-full" ref={scrollRef}>
              <div className="space-y-4">
                {messages.length === 0 && (
                  <div className="flex items-start space-x-3">
                    <div className="w-8 h-8 bg-carbon-blue rounded-full flex items-center justify-center flex-shrink-0">
                      <i className="fas fa-robot text-white text-sm"></i>
                    </div>
                    <div className="flex-1">
                      <p className="text-sm text-gray-900">
                        Hello! I can help you query your knowledge graph. You can ask me questions like "What quality control processes are required for manufacturing equipment?" or "Show me all nodes related to production lines."
                      </p>
                      <p className="text-xs text-carbon-gray-60 mt-1">Just now</p>
                    </div>
                  </div>
                )}

                {messages.map((message: Message) => (
                  <div key={message.id} className={`flex items-start space-x-3 ${message.role === "user" ? "justify-end" : ""}`}>
                    {message.role === "assistant" && (
                      <div className="w-8 h-8 bg-carbon-blue rounded-full flex items-center justify-center flex-shrink-0">
                        <i className="fas fa-robot text-white text-sm"></i>
                      </div>
                    )}
                    <div className={`flex-1 ${message.role === "user" ? "text-right" : ""}`}>
                      {message.role === "user" ? (
                        <div className="inline-block bg-carbon-blue text-white rounded-lg px-4 py-2 max-w-md">
                          <p className="text-sm">{message.content}</p>
                        </div>
                      ) : (
                        <div className="text-sm text-gray-900 whitespace-pre-wrap">{message.content}</div>
                      )}
                      <p className="text-xs text-carbon-gray-60 mt-1">
                        {new Date(message.timestamp).toLocaleTimeString()}
                      </p>
                    </div>
                    {message.role === "user" && (
                      <div className="w-8 h-8 bg-carbon-gray-50 rounded-full flex items-center justify-center flex-shrink-0">
                        <i className="fas fa-user text-carbon-gray-60 text-sm"></i>
                      </div>
                    )}
                  </div>
                ))}

                {sendMessageMutation.isPending && (
                  <div className="flex items-start space-x-3">
                    <div className="w-8 h-8 bg-carbon-blue rounded-full flex items-center justify-center flex-shrink-0">
                      <i className="fas fa-robot text-white text-sm"></i>
                    </div>
                    <div className="flex-1">
                      <p className="text-sm text-gray-900">Thinking...</p>
                    </div>
                  </div>
                )}
              </div>
            </ScrollArea>

            {/* Chat Input */}
            <div className="border-t border-carbon-gray-20 p-4">
              <div className="flex space-x-3">
                <Input
                  value={currentMessage}
                  onChange={(e) => setCurrentMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Ask about your knowledge graph..."
                  disabled={sendMessageMutation.isPending || !sessionId}
                  className="flex-1"
                />
                <Button 
                  onClick={handleSendMessage}
                  disabled={sendMessageMutation.isPending || !sessionId || !currentMessage.trim()}
                >
                  <Send className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </Card>
        </div>

        {/* Query Translation Panel */}
        <div className="space-y-6">
          {/* Quick Queries */}
          <Card>
            <div className="border-b border-carbon-gray-20 p-4">
              <h4 className="font-medium text-gray-900">Quick Queries</h4>
            </div>
            <CardContent className="p-4 space-y-2">
              {quickQueries.map((query) => (
                <Button
                  key={query}
                  variant="ghost"
                  size="sm"
                  className="w-full text-left justify-start text-carbon-blue hover:bg-blue-50"
                  onClick={() => setCurrentMessage(query)}
                >
                  {query}
                </Button>
              ))}
            </CardContent>
          </Card>

          {/* Query Statistics */}
          <Card>
            <div className="border-b border-carbon-gray-20 p-4">
              <h4 className="font-medium text-gray-900">Query Statistics</h4>
            </div>
            <CardContent className="p-4 space-y-3">
              <div className="flex justify-between">
                <span className="text-sm text-carbon-gray-60">Total Queries</span>
                <span className="text-sm font-medium text-gray-900">
                  {metricsData?.totalQueries || 0}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm text-carbon-gray-60">Avg Response Time</span>
                <span className="text-sm font-medium text-gray-900">
                  {metricsData?.avgResponseTime ? `${Math.round(metricsData.avgResponseTime)}ms` : '-'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm text-carbon-gray-60">Success Rate</span>
                <span className="text-sm font-medium text-gray-900">
                  {metricsData?.successRate !== undefined ? `${Math.round(metricsData.successRate * 100)}%` : '-'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm text-carbon-gray-60">Cache Hit Rate</span>
                <span className="text-sm font-medium text-gray-900">
                  {metricsData?.cacheHitRate !== undefined ? `${Math.round(metricsData.cacheHitRate * 100)}%` : '-'}
                </span>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/components/Deduplication.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Slider } from "@/components/ui/slider";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

export default function Deduplication() {
  const [similarityThreshold, setSimilarityThreshold] = useState([85]);
  const [algorithmType, setAlgorithmType] = useState("semantic");
  // Removed unused selectedCandidate state - using direct merge
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: candidatesResponse, refetch: refetchCandidates } = useQuery({
    queryKey: ["/api/duplicates/candidates"],
  });

  const candidates = (candidatesResponse as any)?.candidates || [];

  const runAnalysisMutation = useMutation({
    mutationFn: async ({ threshold, algorithmType }: { threshold: number; algorithmType: string }) => {
      const response = await apiRequest("POST", "/api/duplicates/analyze", { threshold, algorithmType });
      return response.json();
    },
    onSuccess: (data) => {
      refetchCandidates();
      queryClient.invalidateQueries({ queryKey: ["/api/duplicates/stats"] });
      toast({
        title: "Analysis Complete", 
        description: `Found ${data.nodeCandidatesCreated} node duplicates and ${data.relationCandidatesCreated} relation duplicates from ${data.nodesAnalyzed} nodes and ${data.relationsAnalyzed} relations`,
      });
    },
    onError: (error) => {
      toast({
        title: "Analysis Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const mergeCandidateMutation = useMutation({
    mutationFn: async (candidateId: string) => {
      const response = await apiRequest("POST", `/api/duplicates/${candidateId}/merge`, {});
      return response.json();
    },
    onSuccess: () => {
      refetchCandidates();
      queryClient.invalidateQueries({ queryKey: ["/api/nodes/approved"] });
      toast({
        title: "Merge successful",
        description: "The duplicate nodes have been merged successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Merge failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const keepSeparateMutation = useMutation({
    mutationFn: async (candidateId: string) => {
      const response = await apiRequest("POST", `/api/duplicates/${candidateId}/keep-separate`, {});
      return response.json();
    },
    onSuccess: () => {
      refetchCandidates();
      toast({
        title: "Marked as separate",
        description: "The nodes have been marked as separate entities",
      });
    },
    onError: (error) => {
      toast({
        title: "Failed to mark as separate",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const { data: statsResponse } = useQuery({
    queryKey: ["/api/duplicates/stats"],
  });

  const stats = {
    potentialDuplicates: candidates.length,
    autoMerged: (statsResponse as any)?.autoMerged || 0,
    accuracyRate: (statsResponse as any)?.accuracyRate || 0
  };

  return (
    <div className="max-w-5xl mx-auto">
      <div className="mb-6">
        <h3 className="text-lg font-medium text-gray-900">Deduplication Engine</h3>
        <p className="text-carbon-gray-60">Identify and merge similar nodes and relations to maintain graph integrity</p>
      </div>

      {/* Deduplication Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-carbon-yellow bg-opacity-10 rounded-lg flex items-center justify-center">
                <i className="fas fa-exclamation-triangle text-carbon-yellow text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-2xl font-semibold text-gray-900">{stats.potentialDuplicates}</p>
                <p className="text-carbon-gray-60">Potential Duplicates</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-carbon-green bg-opacity-10 rounded-lg flex items-center justify-center">
                <i className="fas fa-compress-arrows-alt text-carbon-green text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-2xl font-semibold text-gray-900">{stats.autoMerged}</p>
                <p className="text-carbon-gray-60">Merged</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-carbon-blue bg-opacity-10 rounded-lg flex items-center justify-center">
                <i className="fas fa-percentage text-carbon-blue text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-2xl font-semibold text-gray-900">{stats.accuracyRate}%</p>
                <p className="text-carbon-gray-60">Merge Rate</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Deduplication Algorithm Settings */}
      <Card className="mb-6">
        <div className="border-b border-carbon-gray-20 p-4">
          <h4 className="font-medium text-gray-900">Algorithm Configuration</h4>
        </div>
        <CardContent className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Similarity Threshold
              </label>
              <div className="flex items-center space-x-4">
                <Slider
                  value={similarityThreshold}
                  onValueChange={setSimilarityThreshold}
                  max={100}
                  min={0}
                  step={1}
                  className="flex-1"
                />
                <span className="text-sm text-carbon-gray-60 w-12">{similarityThreshold[0]}%</span>
              </div>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Algorithm Type
              </label>
              <Select value={algorithmType} onValueChange={setAlgorithmType}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="semantic">Semantic Similarity (AI-based)</SelectItem>
                  <SelectItem value="edit_distance">Edit Distance (String matching)</SelectItem>
                  <SelectItem value="hybrid">Hybrid Approach (Recommended)</SelectItem>
                </SelectContent>
              </Select>
              <p className="text-xs text-carbon-gray-60 mt-1">
                {algorithmType === 'semantic' && 'Uses AI to understand semantic meaning and context'}
                {algorithmType === 'edit_distance' && 'Measures character-level differences between names'}
                {algorithmType === 'hybrid' && 'Combines edit distance and semantic analysis for best results'}
              </p>
            </div>
          </div>
          <div className="mt-6 flex space-x-3">
            <Button 
              onClick={() => runAnalysisMutation.mutate({ 
                threshold: similarityThreshold[0], 
                algorithmType 
              })}
              disabled={runAnalysisMutation.isPending}
            >
              <i className="fas fa-play mr-2"></i>
              {runAnalysisMutation.isPending ? "Analyzing..." : "Run Deduplication"}
            </Button>
            <Button variant="outline">
              <i className="fas fa-save mr-2"></i>
              Save Settings
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Duplicate Candidates */}
      <Card>
        <div className="border-b border-carbon-gray-20 p-4">
          <h4 className="font-medium text-gray-900">Review Duplicate Candidates</h4>
        </div>
        <CardContent className="p-4">
          {candidates.length === 0 ? (
            <div className="text-center py-8 text-carbon-gray-60">
              <i className="fas fa-check-circle text-4xl mb-4"></i>
              <p>No duplicate candidates found</p>
            </div>
          ) : (
            <div className="space-y-4">
              {candidates.map((candidate: any) => (
                <div key={candidate.id} className="border border-carbon-gray-20 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-4">
                    <div className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-carbon-yellow bg-opacity-10 text-carbon-yellow">
                      {candidate.similarityScore}% similarity
                    </div>
                    <div className="flex space-x-2">
                      <Button
                        size="sm"
                        className="bg-carbon-green text-white hover:bg-green-600"
                        onClick={() => mergeCandidateMutation.mutate(candidate.id)}
                        disabled={mergeCandidateMutation.isPending}
                      >
                        <i className="fas fa-compress-arrows-alt mr-1"></i>
                        Choose & Merge
                      </Button>
                      <Button
                        size="sm"
                        variant="destructive"
                        onClick={() => keepSeparateMutation.mutate(candidate.id)}
                        disabled={keepSeparateMutation.isPending}
                      >
                        <i className="fas fa-times mr-1"></i>
                        Keep Separate
                      </Button>
                    </div>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="border border-carbon-gray-20 rounded p-3">
                      <h5 className="font-medium text-gray-900 mb-2">{candidate.node1?.name || "Node A"}</h5>
                      <div className="mb-2">
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                          {candidate.node1?.type || "unknown"}
                        </span>
                      </div>
                      <p className="text-sm text-carbon-gray-60 mb-2">
                        {candidate.node1?.description || "No description available"}
                      </p>
                      <p className="text-xs text-carbon-gray-50">
                        Source: {candidate.node1?.documentName || "Unknown document"}
                      </p>
                    </div>
                    <div className="border border-carbon-gray-20 rounded p-3">
                      <h5 className="font-medium text-gray-900 mb-2">{candidate.node2?.name || "Node B"}</h5>
                      <div className="mb-2">
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                          {candidate.node2?.type || "unknown"}
                        </span>
                      </div>
                      <p className="text-sm text-carbon-gray-60 mb-2">
                        {candidate.node2?.description || "No description available"}
                      </p>
                      <p className="text-xs text-carbon-gray-50">
                        Source: {candidate.node2?.documentName || "Unknown document"}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/DeveloperConsole.t.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { formatRelativeTime } from "@/lib/utils";

interface QueryTranslation {
  id: string;
  naturalLanguageQuery: string;
  graphQuery: string;
  queryType: string;
  executionTime?: number;
  resultCount?: number;
  status: string;
  errorMessage?: string;
  approved: boolean;
  createdAt: string;
}

export default function DeveloperConsole() {
  const [manualQuery, setManualQuery] = useState("");
  const [queryResults, setQueryResults] = useState<any[]>([]);
  const [queryError, setQueryError] = useState<string | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch query translations
  const { data: translationsResponse, isLoading: translationsLoading } = useQuery({
    queryKey: ["/api/dev/query-translations"],
  });

  const translations = translationsResponse?.translations || [];

  // Approve/reject translation mutation
  const approveTranslationMutation = useMutation({
    mutationFn: async ({ id, approved }: { id: string; approved: boolean }) => {
      const response = await apiRequest("PATCH", `/api/dev/query-translations/${id}/approve`, { approved });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/dev/query-translations"] });
      toast({
        title: "Translation updated",
        description: "Query translation approval status updated successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Update failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Execute manual query mutation
  const executeQueryMutation = useMutation({
    mutationFn: async (query: string) => {
      const response = await apiRequest("POST", "/api/dev/execute-query", { query });
      return response.json();
    },
    onSuccess: (data) => {
      setQueryResults(data.results || []);
      setQueryError(null);
      toast({
        title: "Query executed",
        description: `Query executed successfully. Found ${data.results?.length || 0} results.`,
      });
    },
    onError: (error) => {
      setQueryResults([]);
      setQueryError(error.message);
      toast({
        title: "Query failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleExecuteQuery = () => {
    if (!manualQuery.trim()) {
      toast({
        title: "Query required",
        description: "Please enter a query to execute",
        variant: "destructive",
      });
      return;
    }
    executeQueryMutation.mutate(manualQuery);
  };

  const handleValidateQuery = () => {
    // Basic validation - check if it starts with common Cypher keywords
    const cypherKeywords = ['MATCH', 'CREATE', 'MERGE', 'DELETE', 'SET', 'RETURN', 'WHERE'];
    const queryUpper = manualQuery.trim().toUpperCase();

    if (!cypherKeywords.some(keyword => queryUpper.startsWith(keyword))) {
      toast({
        title: "Query validation",
        description: "Query should start with a valid Cypher keyword (MATCH, CREATE, etc.)",
        variant: "destructive",
      });
    } else {
      toast({
        title: "Query validation",
        description: "Query syntax appears valid",
      });
    }
  };

  const handleSaveQuery = () => {
    // Save to localStorage for now
    const savedQueries = JSON.parse(localStorage.getItem('savedQueries') || '[]');
    const newQuery = {
      id: Date.now().toString(),
      query: manualQuery,
      timestamp: new Date().toISOString()
    };
    savedQueries.push(newQuery);
    localStorage.setItem('savedQueries', JSON.stringify(savedQueries));

    toast({
      title: "Query saved",
      description: "Query has been saved to your local collection",
    });
  };

  const getStatusBadge = (translation: QueryTranslation) => {
    if (translation.status === "success") {
      return translation.approved ? (
        <Badge className="bg-carbon-green bg-opacity-10 text-carbon-green">
          <i className="fas fa-thumbs-up mr-1"></i>
          Approved
        </Badge>
      ) : (
        <Badge className="bg-carbon-green bg-opacity-10 text-carbon-green">
          <i className="fas fa-check mr-1"></i>
          Success
        </Badge>
      );
    } else {
      return (
        <Badge className="bg-carbon-red bg-opacity-10 text-carbon-red">
          <i className="fas fa-times mr-1"></i>
          Failed
        </Badge>
      );
    }
  };

  // Mock training data stats
  const trainingStats = {
    totalExamples: 247,
    approvedTranslations: translations.filter((t: QueryTranslation) => t.approved).length,
    pendingReview: translations.filter((t: QueryTranslation) => t.status === "success" && !t.approved).length
  };

  return (
    <div className="max-w-7xl mx-auto">
      <div className="mb-6">
        <h3 className="text-lg font-medium text-gray-900">Developer Console</h3>
        <p className="text-carbon-gray-60">Monitor query translations and improve the natural language processing</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Query Translation Log */}
        <div className="space-y-6">
          <Card>
            <div className="border-b border-carbon-gray-20 p-4">
              <h4 className="font-medium text-gray-900">Recent Query Translations</h4>
            </div>
            <CardContent className="p-4">
              {translationsLoading ? (
                <div className="text-center py-8 text-carbon-gray-60">
                  <i className="fas fa-sync fa-spin text-2xl mb-4"></i>
                  <p>Loading translations...</p>
                </div>
              ) : translations.length === 0 ? (
                <div className="text-center py-8 text-carbon-gray-60">
                  <i className="fas fa-code text-4xl mb-4"></i>
                  <p>No query translations yet</p>
                  <p className="text-sm mt-2">Start chatting to see query translations here</p>
                </div>
              ) : (
                <ScrollArea className="h-96">
                  <div className="space-y-4">
                    {translations.map((translation: QueryTranslation) => (
                      <div key={translation.id} className="border border-carbon-gray-20 rounded-lg p-4">
                        <div className="mb-3">
                          <div className="flex items-center justify-between mb-2">
                            <span className="text-sm font-medium text-gray-900">Natural Language Query:</span>
                            {getStatusBadge(translation)}
                          </div>
                          <p className="text-sm text-carbon-gray-60 bg-carbon-gray-10 p-2 rounded">
                            "{translation.naturalLanguageQuery}"
                          </p>
                        </div>

                        {translation.status === "success" ? (
                          <div className="mb-3">
                            <p className="text-sm font-medium text-gray-900 mb-2">Generated Graph Query:</p>
                            <pre className="text-xs font-ibm-mono bg-gray-900 text-green-400 p-3 rounded overflow-x-auto">
                              {translation.graphQuery || "No query generated"}
                            </pre>
                          </div>
                        ) : (
                          <div className="mb-3">
                            <p className="text-sm font-medium text-gray-900 mb-2">Error:</p>
                            <pre className="text-xs font-ibm-mono bg-red-50 text-red-700 p-3 rounded">
                              {translation.errorMessage || "Unknown error occurred"}
                            </pre>
                          </div>
                        )}

                        <div className="flex items-center justify-between text-xs text-carbon-gray-60 mb-3">
                          <span>Executed: {formatRelativeTime(new Date(translation.createdAt))}</span>
                          {translation.executionTime && (
                            <span>Response time: {translation.executionTime}ms</span>
                          )}
                        </div>

                        {translation.status === "success" && (
                          <div className="flex space-x-2">
                            <Button
                              size="sm"
                              className="bg-carbon-green text-white hover:bg-green-600"
                              onClick={() => approveTranslationMutation.mutate({ id: translation.id, approved: true })}
                              disabled={approveTranslationMutation.isPending || translation.approved}
                            >
                              <i className="fas fa-thumbs-up mr-1"></i>
                              {translation.approved ? "Approved" : "Approve"}
                            </Button>
                            {!translation.approved && (
                              <Button
                                size="sm"
                                className="bg-carbon-yellow text-white hover:bg-yellow-600"
                                onClick={() => {
                                  // For suggest improvement, we could open a modal or form
                                  toast({
                                    title: "Feature coming soon",
                                    description: "Suggestion interface will be available in a future update",
                                  });
                                }}
                              >
                                <i className="fas fa-edit mr-1"></i>
                                Suggest Improvement
                              </Button>
                            )}
                          </div>
                        )}

                        {translation.status === "failed" && (
                          <div className="flex space-x-2">
                            <Button
                              size="sm"
                              onClick={() => {
                                toast({
                                  title: "Feature coming soon",
                                  description: "Training example interface will be available in a future update",
                                });
                              }}
                            >
                              <i className="fas fa-plus mr-1"></i>
                              Add Training Example
                            </Button>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </ScrollArea>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Query Builder & Testing */}
        <div className="space-y-6">
          {/* Manual Query Builder */}
          <Card>
            <div className="border-b border-carbon-gray-20 p-4">
              <h4 className="font-medium text-gray-900">Manual Query Builder</h4>
            </div>
            <CardContent className="p-4">
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Graph Query (Cypher)
                  </label>
                  <Textarea
                    value={manualQuery}
                    onChange={(e) => setManualQuery(e.target.value)}
                    placeholder="MATCH (n:Node) RETURN n"
                    className="h-32 font-ibm-mono text-sm"
                  />
                </div>
                <div className="flex space-x-3">
                  <Button
                    onClick={handleExecuteQuery}
                    disabled={executeQueryMutation.isPending}
                    className="text-sm"
                  >
                    <i className="fas fa-play mr-2"></i>
                    {executeQueryMutation.isPending ? "Executing..." : "Execute Query"}
                  </Button>
                  <Button
                    variant="outline"
                    onClick={handleValidateQuery}
                    className="text-sm"
                  >
                    <i className="fas fa-check mr-2"></i>
                    Validate
                  </Button>
                  <Button
                    variant="outline"
                    onClick={handleSaveQuery}
                    disabled={!manualQuery.trim()}
                    className="text-sm"
                  >
                    <i className="fas fa-save mr-2"></i>
                    Save
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Query Results */}
          <Card>
            <div className="border-b border-carbon-gray-20 p-4">
              <h4 className="font-medium text-gray-900">Query Results</h4>
            </div>
            <CardContent className="p-4">
              <div className="bg-carbon-gray-10 rounded-lg p-4 h-48 overflow-auto">
                {executeQueryMutation.isPending ? (
                  <div className="flex items-center justify-center h-full text-carbon-gray-60">
                    <i className="fas fa-sync fa-spin mr-2"></i>
                    <span>Executing query...</span>
                  </div>
                ) : queryError ? (
                  <pre className="text-sm font-ibm-mono text-red-600 whitespace-pre-wrap">
                    Error: {queryError}
                  </pre>
                ) : queryResults.length > 0 ? (
                  <pre className="text-sm font-ibm-mono text-gray-800 whitespace-pre-wrap">
                    {JSON.stringify(queryResults, null, 2)}
                  </pre>
                ) : (
                  <pre className="text-sm font-ibm-mono text-carbon-gray-60">
                    Query not executed yet.{"\n"}Run a query to see results here.
                  </pre>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Training Data Management */}
          <Card>
            <div className="border-b border-carbon-gray-20 p-4">
              <h4 className="font-medium text-gray-900">Training Data</h4>
            </div>
            <CardContent className="p-4">
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-sm text-carbon-gray-60">Training Examples</span>
                  <span className="text-sm font-medium text-gray-900">{trainingStats.totalExamples}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-carbon-gray-60">Approved Translations</span>
                  <span className="text-sm font-medium text-gray-900">{trainingStats.approvedTranslations}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-carbon-gray-60">Pending Review</span>
                  <span className="text-sm font-medium text-gray-900">{trainingStats.pendingReview}</span>
                </div>
                <div className="pt-3 border-t border-carbon-gray-20">
                  <Button
                    className="w-full text-sm"
                    onClick={() => {
                      toast({
                        title: "Feature coming soon",
                        description: "Model retraining will be available in a future update",
                      });
                    }}
                  >
                    <i className="fas fa-sync mr-2"></i>
                    Retrain Translation Model
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

