import { GraphCypherQAChain } from '@langchain/community/chains/graph_qa/cypher';
import { Neo4jGraph } from '@langchain/community/graphs/neo4j_graph';
import { BaseChatModel } from '@langchain/core/language_models/chat_models';
import { modelProvider } from '../llm/modelProvider';

export interface GraphRAGResult {
  success: boolean;
  method: string;
  stage: number;
  question?: string;
  answer?: string;
  cypher?: string;
  context?: any;
  processingTime?: number;
  error?: string;
  suggestion?: string;
  metadata?: {
    llmModel: string;
    returnedIntermediateSteps: boolean;
  };
}

export class LangChainGraphRAG {
  private graph: Neo4jGraph | null = null;
  private llm: BaseChatModel;
  private qaChain: GraphCypherQAChain | null = null;
  private initializationPromise: Promise<void> | null = null;
  private initialized = false;

  constructor() {
    // Use the model provider to get the current model
    this.llm = modelProvider.getLangChainModel();

    this.initializationPromise = this.initialize();
  }

  async refreshModel(): Promise<void> {
    // Get the new model from the provider
    this.llm = modelProvider.getLangChainModel();

    // Reinitialize the QA chain with the new model
    if (this.graph && this.initialized) {
      await this.initializeQAChain();
    }
  }

  private async initializeQAChain(): Promise<void> {
    // Create the GraphCypher QA Chain with custom prompt
    const cypherPrompt = `Task: Generate a Cypher query to retrieve information from a Neo4j graph database based on the user's question.

IMPORTANT CONTEXT:
- Nodes have BOTH dynamic labels (like :recipe, :entity, :process) AND a 'type' property
- All nodes have these properties: id, name, description, type
- The 'type' property value matches the node label (in lowercase)
- You can query by label: MATCH (n:recipe) or by property: MATCH (n) WHERE n.type = 'recipe'

Instructions:
1. Use the provided schema for node labels and relationship types
2. For general "show nodes" queries, return comprehensive data: name, type, description
3. Use case-insensitive matching with toLower() for text comparisons
4. Default LIMIT 25 for general queries, more for specific searches
5. Return meaningful aliases for clarity

Common Patterns:
- Show all nodes: MATCH (n) RETURN n.name as name, n.type as type, n.description as description LIMIT 25
- Find by type: MATCH (n:ingredient) RETURN n OR MATCH (n) WHERE n.type = 'ingredient' RETURN n
- Find by name: MATCH (n) WHERE toLower(n.name) CONTAINS toLower('search') RETURN n
- Count by type: MATCH (n) RETURN n.type as type, count(*) as count ORDER BY count DESC

Schema:
{schema}

User Question: {question}

Cypher Query:`;

    this.qaChain = GraphCypherQAChain.fromLLM({
      llm: this.llm,
      graph: this.graph!,
      verbose: process.env.NODE_ENV === 'development',
      returnIntermediateSteps: true,
      cypherPrompt,
      returnDirect: false,
      topK: 10,
    });
  }

  private async initialize(): Promise<void> {
    try {
      console.log('[LangChainGraphRAG] Initializing Neo4j Graph connection...');

      const neo4jUri = process.env.NEO4J_URI || 'bolt://localhost:7687';
      const neo4jUsername = process.env.NEO4J_USERNAME || 'neo4j';
      const neo4jPassword = process.env.NEO4J_PASSWORD || '';

      if (!neo4jPassword) {
        throw new Error('NEO4J_PASSWORD must be provided');
      }

      // Initialize Neo4j Graph connection
      this.graph = await Neo4jGraph.initialize({
        url: neo4jUri,
        username: neo4jUsername,
        password: neo4jPassword,
      });

      console.log('[LangChainGraphRAG] Graph connection established');

      // Refresh the schema
      await this.graph.refreshSchema();
      console.log('[LangChainGraphRAG] Schema refreshed');

      // Initialize the QA chain
      await this.initializeQAChain();

      this.initialized = true;
      console.log('[LangChainGraphRAG] Initialization complete');
    } catch (error) {
      console.error('[LangChainGraphRAG] Initialization failed:', error);
      this.initialized = false;
      throw error;
    }
  }

  private async ensureInitialized(): Promise<void> {
    if (this.initializationPromise) {
      await this.initializationPromise;
    }
    if (!this.initialized || !this.qaChain || !this.graph) {
      throw new Error('LangChainGraphRAG not properly initialized');
    }
  }

  async queryGraph(question: string, options: {
    domain?: string;
    includeExamples?: boolean;
    maxRetries?: number;
  } = {}): Promise<GraphRAGResult> {
    await this.ensureInitialized();

    const startTime = Date.now();
    const maxRetries = options.maxRetries || 2;
    let lastError: Error | null = null;

    for (let attempt = 0; attempt < maxRetries; attempt++) {
      try {
        console.log(`[LangChainGraphRAG] Processing query (attempt ${attempt + 1}): "${question}"`);

        // Enhance question with domain context
        const enhancedQuestion = this.enhanceQuestion(question, options);

        // Execute the chain
        const result = await this.qaChain!.invoke({
          query: enhancedQuestion,
        });

        const processingTime = Date.now() - startTime;

        // Extract cypher query from intermediate steps
        let cypherQuery: string | undefined;
        let context: any;

        if (result.intermediateSteps && Array.isArray(result.intermediateSteps)) {
          const cypherStep = result.intermediateSteps.find((step: any) => step.query);
          if (cypherStep) {
            cypherQuery = cypherStep.query;
            context = cypherStep.context;
          }
        }

        console.log(`[LangChainGraphRAG] Query successful in ${processingTime}ms`);

        return {
          success: true,
          method: 'langchain-graphcypher',
          stage: 4,
          question: enhancedQuestion,
          answer: result.result || result.text || 'No results found',
          cypher: cypherQuery,
          context,
          processingTime,
          metadata: {
            llmModel: modelProvider.getCurrentConfig().model,
            returnedIntermediateSteps: !!result.intermediateSteps
          }
        };

      } catch (error) {
        console.error(`[LangChainGraphRAG] Query attempt ${attempt + 1} failed:`, error);
        lastError = error as Error;

        if (attempt < maxRetries - 1) {
          // Wait before retry
          await new Promise(resolve => setTimeout(resolve, 1000 * (attempt + 1)));
        }
      }
    }

    // All attempts failed
    const processingTime = Date.now() - startTime;

    return {
      success: false,
      method: 'langchain-graphcypher',
      stage: 4,
      error: lastError?.message || 'Unknown error',
      suggestion: this.generateErrorSuggestion(lastError),
      processingTime
    };
  }

  private enhanceQuestion(question: string, options: { domain?: string; includeExamples?: boolean }): string {
    let enhancedQuestion = question;

    // Add domain context
    if (options.domain === 'recipe') {
      enhancedQuestion = `In the context of recipes, ingredients, and cooking: ${question}`;
    } else if (options.domain === 'technical') {
      enhancedQuestion = `In the context of technical systems and components: ${question}`;
    } else if (options.domain === 'process') {
      enhancedQuestion = `In the context of processes and procedures: ${question}`;
    }

    // Add instruction for better responses
    if (options.includeExamples) {
      enhancedQuestion += '\nProvide specific details and include all relevant relationships.';
    }

    return enhancedQuestion;
  }

  private generateErrorSuggestion(error: Error | null): string {
    if (!error) return 'Please try rephrasing your question';

    const message = error.message.toLowerCase();

    if (message.includes('syntax')) {
      return 'Try rephrasing your question more clearly or use simpler terms';
    } else if (message.includes('timeout')) {
      return 'Your query might be too complex. Try breaking it into smaller questions';
    } else if (message.includes('rate limit')) {
      return 'API rate limit reached. Please wait a moment before trying again';
    } else if (message.includes('connection') || message.includes('driver')) {
      return 'Database connection issue. Please try again in a moment';
    } else if (message.includes('schema')) {
      return 'The query structure might not match the database. Try using different entity names';
    }

    return 'Please try rephrasing your question or contact support if the issue persists';
  }

  async refreshSchema(): Promise<{ success: boolean; error?: string }> {
    try {
      await this.ensureInitialized();
      await this.graph!.refreshSchema();
      console.log('[LangChainGraphRAG] Schema refreshed successfully');
      return { success: true };
    } catch (error) {
      console.error('[LangChainGraphRAG] Failed to refresh schema:', error);
      return { 
        success: false, 
        error: error instanceof Error ? error.message : 'Unknown error' 
      };
    }
  }

  async getGraphSchema(): Promise<string | null> {
    try {
      await this.ensureInitialized();
      return this.graph!.getSchema();
    } catch (error) {
      console.error('[LangChainGraphRAG] Failed to get schema:', error);
      return null;
    }
  }

  async testConnection(): Promise<boolean> {
    try {
      await this.ensureInitialized();
      // Try a simple query to test connection
      const result = await this.graph!.query('MATCH (n) RETURN count(n) as count LIMIT 1');
      return true;
    } catch (error) {
      console.error('[LangChainGraphRAG] Connection test failed:', error);
      return false;
    }
  }
}

server/services/graphrag/nlpProcessor.ts.content:
import natural from 'natural';

interface Intent {
  intent: string;
  confidence: number;
}

interface ExtractedEntities {
  [key: string]: string[];
}

export interface NLPResult {
  originalQuery: string;
  intent: Intent;
  entities: ExtractedEntities;
  keywords: string[];
  confidence: number;
}

export class NLPProcessor {
  private tokenizer: natural.WordTokenizer;
  private tfidf: natural.TfIdf;
  private intentClassifier: Record<string, string[]>;
  private entityPatterns: Record<string, RegExp>;
  private stopWords: Set<string>;

  constructor() {
    this.tokenizer = new natural.WordTokenizer();
    this.tfidf = new natural.TfIdf();
    this.intentClassifier = this.initializeIntentClassifier();
    this.entityPatterns = this.initializeEntityPatterns();
    this.stopWords = new Set([
      'the', 'a', 'an', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 
      'of', 'with', 'by', 'from', 'as', 'is', 'was', 'are', 'were', 'been',
      'be', 'has', 'had', 'does', 'did', 'will', 'would', 'could',
      'should', 'may', 'might', 'must', 'can', 'could', 'shall', 'should'
      // Removed 'what', 'which', 'do', 'have', 'how' as they can be important for query understanding
    ]);
  }

  private initializeIntentClassifier(): Record<string, string[]> {
    return {
      'list_ingredients': ['ingredient', 'ingredients'],
      'list_recipes': ['recipe', 'recipes', 'dish', 'dishes', 'meal', 'meals'],
      'find_procedures': ['step', 'steps', 'instruction', 'instructions', 'how', 'procedure', 'process', 'method', 'way'],
      'find_properties': ['time', 'duration', 'temperature', 'size', 'weight', 'amount', 'quantity', 'measure'],
      'find_relationships': ['connected', 'related', 'linked', 'depends', 'requires', 'uses', 'produces', 'affects'],
      'count_entities': ['count', 'number', 'many', 'much', 'total'],
      'list_entities': ['list', 'show', 'display', 'all', 'every', 'each', 'get', 'find', 'which', 'what', 'nodes', 'have', 'exist', 'see'],
      'describe_entity': ['describe', 'explain', 'tell', 'about', 'details', 'information'],
      'find_components': ['component', 'part', 'module', 'system', 'unit', 'element', 'piece', 'contains', 'made', 'consists', 'includes'],
      'analyze_network': ['network', 'graph', 'connection', 'path', 'route', 'chain', 'link']
    };
  }

  private initializeEntityPatterns(): Record<string, RegExp> {
    return {
      // Capitalized words (likely proper nouns/entity names)
      properNouns: /\b([A-Z][a-z]+(?:\s+[A-Z][a-z]+)*)\b/g,
      // Quoted strings
      quotedEntities: /["']([^"']+)["']/g,
      // Common measurement patterns
      quantities: /\b(\d+(?:\.\d+)?)\s*(cup|cups|tablespoon|tbsp|teaspoon|tsp|pound|lb|ounce|oz|gram|g|kilogram|kg|ml|liter|l)s?\b/gi,
      // Time patterns
      timeUnits: /\b(\d+)\s*(minute|minutes|hour|hours|second|seconds|day|days)s?\b/gi,
      // Temperature patterns
      temperatures: /\b(\d+)\s*(?:Â°|degree|degrees)\s*([CF]|celsius|fahrenheit)?\b/gi,
      // Alphanumeric identifiers
      identifiers: /\b([A-Z0-9]{2,}(?:[-_][A-Z0-9]+)*)\b/g
    };
  }

  async processQuery(userQuery: string): Promise<NLPResult> {
    const intent = this.classifyIntent(userQuery);
    const entities = this.extractEntities(userQuery);
    const keywords = this.extractKeywords(userQuery);
    const confidence = this.calculateConfidence(intent, entities, keywords);

    return {
      originalQuery: userQuery,
      intent,
      entities,
      keywords,
      confidence
    };
  }

  private classifyIntent(query: string): Intent {
    const tokens = this.tokenizer.tokenize(query.toLowerCase()) || [];
    const queryLower = query.toLowerCase();
    const scores = new Map<string, number>();

    // Special patterns for specific intents  
    // Check for queries asking about ingredients OF something specific
    const hasSpecificEntity = tokens.some(t => t.length > 3 && t[0] === t[0].toUpperCase()) || 
                             tokens.includes('flammkuchen') || 
                             queryLower.includes('for ') || 
                             queryLower.includes('in ') ||
                             queryLower.includes('of ');

    if (queryLower.includes('ingredients') && hasSpecificEntity) {
      // "ingredients for X", "ingredients needed for X", "ingredients in X", "ingredients of X"
      return {
        intent: 'find_ingredients',
        confidence: 0.9
      };
    }

    if ((queryLower.includes('list') || queryLower.includes('show') || queryLower.includes('all')) && queryLower.includes('ingredients')) {
      // "list all ingredients", "show me all ingredients"
      return {
        intent: 'list_ingredients',
        confidence: 0.9
      };
    }

    // Calculate scores for each intent
    for (const [intent, keywords] of Object.entries(this.intentClassifier)) {
      let score = 0;
      let matches = 0;

      for (const keyword of keywords) {
        if (tokens.some(token => token.includes(keyword) || keyword.includes(token))) {
          score += 1;
          matches++;
        }
      }

      // Normalize by number of keywords
      if (matches > 0) {
        scores.set(intent, score / keywords.length);
      }
    }

    // Find best matching intent
    let bestIntent = 'unknown';
    let maxScore = 0;

    scores.forEach((score, intent) => {
      if (score > maxScore) {
        maxScore = score;
        bestIntent = intent;
      }
    });

    return {
      intent: bestIntent,
      confidence: Math.min(maxScore, 1.0)
    };
  }

  private extractEntities(query: string): ExtractedEntities {
    const entities: ExtractedEntities = {};

    // Extract proper nouns
    const properNouns = Array.from(query.matchAll(this.entityPatterns.properNouns));
    if (properNouns.length > 0) {
      entities.properNouns = properNouns.map(match => match[1]);
    }

    // Extract quoted entities
    const quoted = Array.from(query.matchAll(this.entityPatterns.quotedEntities));
    if (quoted.length > 0) {
      entities.quotedEntities = quoted.map(match => match[1]);
    }

    // Extract quantities
    const quantities = Array.from(query.matchAll(this.entityPatterns.quantities));
    if (quantities.length > 0) {
      entities.quantities = quantities.map(match => match[0]);
    }

    // Extract time units
    const timeUnits = Array.from(query.matchAll(this.entityPatterns.timeUnits));
    if (timeUnits.length > 0) {
      entities.timeUnits = timeUnits.map(match => match[0]);
    }

    // Extract temperatures
    const temperatures = Array.from(query.matchAll(this.entityPatterns.temperatures));
    if (temperatures.length > 0) {
      entities.temperatures = temperatures.map(match => match[0]);
    }

    // Extract identifiers
    const identifiers = Array.from(query.matchAll(this.entityPatterns.identifiers));
    if (identifiers.length > 0) {
      entities.identifiers = identifiers.map(match => match[1]);
    }

    // Extract potential entity names (multi-word sequences not in stop words)
    const tokens = this.tokenizer.tokenize(query.toLowerCase()) || [];
    const potentialEntities: string[] = [];

    for (let i = 0; i < tokens.length; i++) {
      if (!this.stopWords.has(tokens[i]) && tokens[i].length > 2) {
        // Single word entity
        potentialEntities.push(tokens[i]);

        // Try to build multi-word entities
        if (i < tokens.length - 1 && !this.stopWords.has(tokens[i + 1])) {
          potentialEntities.push(`${tokens[i]} ${tokens[i + 1]}`);
        }
      }
    }

    if (potentialEntities.length > 0) {
      entities.candidates = potentialEntities;
    }

    return entities;
  }

  private extractKeywords(query: string): string[] {
    const tokens = this.tokenizer.tokenize(query.toLowerCase()) || [];

    // Filter out stop words and short tokens
    const keywords = tokens.filter(token => 
      !this.stopWords.has(token) && 
      token.length > 2 &&
      !/^\d+$/.test(token) // Not just numbers
    );

    // Remove duplicates and return
    return Array.from(new Set(keywords));
  }

  private calculateConfidence(intent: Intent, entities: ExtractedEntities, keywords: string[]): number {
    let confidence = 0;

    // Intent confidence contributes 40%
    confidence += intent.confidence * 0.4;

    // Entity extraction contributes 30%
    const entityScore = Math.min(Object.keys(entities).length * 0.1, 0.3);
    confidence += entityScore;

    // Keyword richness contributes 30%
    const keywordScore = Math.min(keywords.length * 0.05, 0.3);
    confidence += keywordScore;

    return Math.min(confidence, 1.0);
  }

  suggestQueryImprovements(nlpResult: NLPResult): string[] {
    const suggestions: string[] = [];

    if (nlpResult.confidence < 0.3) {
      suggestions.push('Try using more specific terms or entity names');
    }

    if (nlpResult.intent.intent === 'unknown') {
      suggestions.push('Consider using action words like: find, show, list, count, describe');
    }

    if (Object.keys(nlpResult.entities).length === 0) {
      suggestions.push('Include specific names or identifiers in your query');
    }

    if (nlpResult.keywords.length < 2) {
      suggestions.push('Add more descriptive keywords to your query');
    }

    // Intent-specific suggestions
    switch (nlpResult.intent.intent) {
      case 'find_ingredients':
        suggestions.push('Example: "What ingredients are in chocolate cake?"');
        break;
      case 'find_recipes':
        suggestions.push('Example: "Show me recipes with tomatoes"');
        break;
      case 'find_relationships':
        suggestions.push('Example: "How is flour related to bread?"');
        break;
      case 'count_entities':
        suggestions.push('Example: "How many ingredients do we have?"');
        break;
    }

    return suggestions;
  }
}

server/services/graphrag/queryOrchestrat.content:
import { Neo4jService } from '../neo4j';
import { TemplateQueryEngine } from './templateQueryEngine';
import { NLPProcessor, NLPResult } from './nlpProcessor';
import { LangChainGraphRAG } from './langchainGraphRAG';
import type { GraphRAGResult } from './langchainGraphRAG';
import { LangfuseService } from '../langfuse';

export interface QueryResult {
  success: boolean;
  stage: number;
  method: string;
  query: string;
  cypher?: string;
  results?: any[];
  answer?: string;
  error?: string;
  suggestions?: string[];
  processingTime: number;
  nlpAnalysis?: NLPResult;
  confidence?: number;
  metadata?: any;
  fromCache?: boolean;
  timestamp?: number;
}

interface QueryStrategy {
  approach: 'progressive' | 'direct-llm' | 'hybrid-parallel' | 'template-first';
  complexity: {
    score: number;
    indicators: string[];
    length: number;
    hasNegation: boolean;
  };
}

export class QueryOrchestrator {
  private templateEngine: TemplateQueryEngine;
  private nlpProcessor: NLPProcessor;
  private langchainGraphRAG: LangChainGraphRAG;
  private langfuseService: LangfuseService;
  private queryCache: Map<string, QueryResult>;
  private metrics: {
    queries: number;
    cacheHits: number;
    successByStage: Record<number, number>;
    totalLatency: Record<number, number>;
    errorsByStage: Record<number, number>;
  };

  constructor(private neo4jService: Neo4jService) {
    this.templateEngine = new TemplateQueryEngine(neo4jService);
    this.nlpProcessor = new NLPProcessor();
    this.langchainGraphRAG = new LangChainGraphRAG();
    this.langfuseService = new LangfuseService();
    this.queryCache = new Map();

    this.metrics = {
      queries: 0,
      cacheHits: 0,
      successByStage: { 1: 0, 2: 0, 3: 0, 4: 0 },
      totalLatency: { 1: 0, 2: 0, 3: 0, 4: 0 },
      errorsByStage: { 1: 0, 2: 0, 3: 0, 4: 0 }
    };
  }

  async processQuery(
    userQuery: string, 
    options: {
      skipCache?: boolean;
      skipTemplates?: boolean;
      skipNLP?: boolean;
      skipLangfuse?: boolean;
      skipLangChain?: boolean;
      forceStrategy?: string;
      maxRetries?: number;
    } = {}
  ): Promise<QueryResult> {
    const startTime = Date.now();
    this.metrics.queries++;

    console.log(`[QueryOrchestrator] Processing query: "${userQuery}"`);
    console.log(`[QueryOrchestrator] Options:`, options);

    // Check cache
    if (!options.skipCache) {
      const cacheKey = this.generateCacheKey(userQuery, options);
      if (this.queryCache.has(cacheKey)) {
        const cached = this.queryCache.get(cacheKey)!;
        console.log(`[QueryOrchestrator] Cache hit for query`);
        this.metrics.cacheHits++;
        return { ...cached, fromCache: true };
      }
    }

    // Determine strategy
    const strategy = this.determineStrategy(userQuery, options);
    console.log(`[QueryOrchestrator] Using strategy: ${strategy.approach}, complexity: ${strategy.complexity.score}`);

    let result: QueryResult | null = null;

    try {
      switch (strategy.approach) {
        case 'template-first':
          result = await this.templateFirstProcessing(userQuery, options);
          break;
        case 'progressive':
          result = await this.progressiveProcessing(userQuery, options);
          break;
        case 'direct-llm':
          result = await this.directLLMProcessing(userQuery, options);
          break;
        case 'hybrid-parallel':
          result = await this.hybridParallelProcessing(userQuery, options);
          break;
        default:
          result = await this.progressiveProcessing(userQuery, options);
      }

      // Update metrics
      if (result.success) {
        this.metrics.successByStage[result.stage] = (this.metrics.successByStage[result.stage] || 0) + 1;
        this.metrics.totalLatency[result.stage] = (this.metrics.totalLatency[result.stage] || 0) + result.processingTime;
      } else {
        this.metrics.errorsByStage[result.stage || 0] = (this.metrics.errorsByStage[result.stage || 0] || 0) + 1;
      }

      // Cache successful results
      if (result.success && !options.skipCache) {
        const cacheKey = this.generateCacheKey(userQuery, options);
        this.cacheResult(cacheKey, result);
      }

    } catch (error) {
      console.error(`[QueryOrchestrator] Processing failed:`, error);
      result = {
        success: false,
        stage: 0,
        method: 'error',
        query: userQuery,
        error: error instanceof Error ? error.message : 'Unknown error',
        processingTime: Date.now() - startTime
      };
    }

    result.processingTime = Date.now() - startTime;
    console.log(`[QueryOrchestrator] Completed in ${result.processingTime}ms - Success: ${result.success} - Stage: ${result.stage}`);

    return result;
  }

  private determineStrategy(userQuery: string, options: any): QueryStrategy {
    const complexity = this.analyzeQueryComplexity(userQuery);

    if (options.forceStrategy) {
      return { approach: options.forceStrategy, complexity };
    }

    // Simple queries - use templates first
    if (complexity.score < 0.3) {
      return { approach: 'template-first', complexity };
    }

    // Moderate complexity - progressive approach
    if (complexity.score < 0.6) {
      return { approach: 'progressive', complexity };
    }

    // High complexity - try parallel processing
    if (complexity.score < 0.8) {
      return { approach: 'hybrid-parallel', complexity };
    }

    // Very complex - go straight to LLM
    return { approach: 'direct-llm', complexity };
  }

  private analyzeQueryComplexity(userQuery: string): QueryStrategy['complexity'] {
    const complexityIndicators = [
      'multi-hop', 'relationship', 'connected', 'path', 'chain',
      'analyze', 'compare', 'aggregate', 'count', 'average',
      'most', 'least', 'best', 'worst', 'similar', 'related',
      'between', 'through', 'via', 'complex', 'detailed'
    ];

    const tokens = userQuery.toLowerCase().split(/\s+/);
    let complexityScore = 0;
    const foundIndicators: string[] = [];

    for (const indicator of complexityIndicators) {
      if (tokens.some(token => token.includes(indicator))) {
        complexityScore += 0.15;
        foundIndicators.push(indicator);
      }
    }

    // Long queries are often more complex
    if (tokens.length > 10) complexityScore += 0.2;
    if (tokens.length > 20) complexityScore += 0.2;

    // Questions with multiple entities
    const properNouns = userQuery.match(/\b[A-Z][a-z]+\b/g) || [];
    if (properNouns.length > 2) complexityScore += 0.2;

    // Negations add complexity
    const hasNegation = tokens.some(token => ['not', 'without', 'except', 'excluding'].includes(token));
    if (hasNegation) complexityScore += 0.2;

    return {
      score: Math.min(complexityScore, 1.0),
      indicators: foundIndicators,
      length: tokens.length,
      hasNegation
    };
  }

  private async templateFirstProcessing(userQuery: string, options: any): Promise<QueryResult> {
    const startTime = Date.now();

    // Stage 1: Try template matching (fastest)
    if (!options.skipTemplates) {
      console.log(`[QueryOrchestrator] Stage 1: Template matching`);
      const templateResult = await this.templateEngine.processQuery(userQuery);

      if (templateResult.success && templateResult.results !== undefined) {
        return {
          success: true,
          stage: 1,
          method: templateResult.method,
          query: userQuery,
          cypher: templateResult.cypher,
          results: templateResult.results,
          processingTime: Date.now() - startTime,
          confidence: 0.9
        };
      }
    }

    // Fall back to progressive if templates fail
    return this.progressiveProcessing(userQuery, options);
  }

  private async progressiveProcessing(userQuery: string, options: any): Promise<QueryResult> {
    const startTime = Date.now();

    // Stage 1: Template matching
    if (!options.skipTemplates) {
      console.log(`[QueryOrchestrator] Stage 1: Template matching`);
      const templateResult = await this.templateEngine.processQuery(userQuery);

      if (templateResult.success && templateResult.results !== undefined) {
        return {
          success: true,
          stage: 1,
          method: templateResult.method,
          query: userQuery,
          cypher: templateResult.cypher,
          results: templateResult.results,
          processingTime: Date.now() - startTime,
          confidence: 0.9
        };
      }
    }

    // Stage 2: NLP-enhanced processing
    if (!options.skipNLP) {
      console.log(`[QueryOrchestrator] Stage 2: NLP processing`);
      const nlpResult = await this.nlpProcessor.processQuery(userQuery);
      console.log(`[QueryOrchestrator] NLP Intent: ${nlpResult.intent.intent}, Keywords:`, nlpResult.keywords);

      if (nlpResult.confidence > 0.3) {
        const enhancedQuery = await this.generateNLPEnhancedQuery(nlpResult);

        if (enhancedQuery.success && enhancedQuery.results) {
          return {
            success: true,
            stage: 2,
            method: 'nlp-enhanced',
            query: userQuery,
            cypher: enhancedQuery.cypher,
            results: enhancedQuery.results,
            nlpAnalysis: nlpResult,
            processingTime: Date.now() - startTime,
            confidence: nlpResult.confidence
          };
        }
      }
    }

    // Stage 3: Langfuse Claude translation (existing approach)
    if (!options.skipLangfuse) {
      console.log(`[QueryOrchestrator] Stage 3: Langfuse Claude translation`);
      try {
        const translation = await this.langfuseService.translateNaturalLanguageQuery(userQuery);

        if (translation.graphQuery) {
          const results = await this.neo4jService.executeQuery(translation.graphQuery);

          return {
            success: true,
            stage: 3,
            method: 'langfuse-claude',
            query: userQuery,
            cypher: translation.graphQuery,
            results,
            processingTime: Date.now() - startTime,
            confidence: 0.7,
            metadata: {
              queryType: translation.queryType,
              explanation: translation.explanation
            }
          };
        }
      } catch (error) {
        console.error(`[QueryOrchestrator] Langfuse translation failed:`, error);
      }
    }

    // Stage 4: LangChain GraphCypherQAChain (most powerful)
    if (!options.skipLangChain) {
      console.log(`[QueryOrchestrator] Stage 4: LangChain GraphRAG`);
      const graphRAGResult = await this.langchainGraphRAG.queryGraph(userQuery, {
        includeExamples: true,
        maxRetries: options.maxRetries || 2
      });

      if (graphRAGResult.success) {
        return {
          success: true,
          stage: 4,
          method: graphRAGResult.method,
          query: userQuery,
          cypher: graphRAGResult.cypher,
          answer: graphRAGResult.answer,
          processingTime: Date.now() - startTime,
          confidence: 0.8,
          metadata: graphRAGResult.metadata
        };
      }
    }

    // All stages failed
    return {
      success: false,
      stage: 0,
      method: 'none',
      query: userQuery,
      error: 'Unable to process query using any method',
      suggestions: await this.generateFailureSuggestions(userQuery),
      processingTime: Date.now() - startTime
    };
  }

  private async directLLMProcessing(userQuery: string, options: any): Promise<QueryResult> {
    const startTime = Date.now();

    console.log(`[QueryOrchestrator] Direct LLM processing`);

    // Go straight to LangChain GraphRAG for complex queries
    const graphRAGResult = await this.langchainGraphRAG.queryGraph(userQuery, {
      includeExamples: true,
      maxRetries: options.maxRetries || 3
    });

    if (graphRAGResult.success) {
      return {
        success: true,
        stage: 4,
        method: graphRAGResult.method,
        query: userQuery,
        cypher: graphRAGResult.cypher,
        answer: graphRAGResult.answer,
        processingTime: Date.now() - startTime,
        confidence: 0.8,
        metadata: graphRAGResult.metadata
      };
    }

    // Fall back to progressive if direct LLM fails
    return this.progressiveProcessing(userQuery, { ...options, skipLangChain: true });
  }

  private async hybridParallelProcessing(userQuery: string, options: any): Promise<QueryResult> {
    const startTime = Date.now();

    console.log(`[QueryOrchestrator] Hybrid parallel processing`);

    // Run multiple approaches in parallel
    const promises: Promise<any>[] = [];

    if (!options.skipTemplates) {
      promises.push(this.templateEngine.processQuery(userQuery));
    }

    if (!options.skipNLP) {
      promises.push(this.nlpProcessor.processQuery(userQuery));
    }

    if (!options.skipLangfuse) {
      promises.push(this.langfuseService.translateNaturalLanguageQuery(userQuery).catch(e => null));
    }

    const results = await Promise.allSettled(promises);

    // Process template result
    if (results[0]?.status === 'fulfilled' && results[0].value?.success) {
      return {
        success: true,
        stage: 1,
        method: 'template',
        query: userQuery,
        cypher: results[0].value.cypher,
        results: results[0].value.results,
        processingTime: Date.now() - startTime,
        confidence: 0.9
      };
    }

    // Process NLP result
    if (results[1]?.status === 'fulfilled' && results[1].value?.confidence > 0.3) {
      const enhancedQuery = await this.generateNLPEnhancedQuery(results[1].value);
      if (enhancedQuery.success) {
        return {
          success: true,
          stage: 2,
          method: 'nlp-enhanced',
          query: userQuery,
          cypher: enhancedQuery.cypher,
          results: enhancedQuery.results,
          nlpAnalysis: results[1].value,
          processingTime: Date.now() - startTime,
          confidence: results[1].value.confidence
        };
      }
    }

    // Process Langfuse result
    if (results[2]?.status === 'fulfilled' && results[2].value?.graphQuery) {
      try {
        const queryResults = await this.neo4jService.executeQuery(results[2].value.graphQuery);
        return {
          success: true,
          stage: 3,
          method: 'langfuse-claude',
          query: userQuery,
          cypher: results[2].value.graphQuery,
          results: queryResults,
          processingTime: Date.now() - startTime,
          confidence: 0.7
        };
      } catch (error) {
        console.error(`[QueryOrchestrator] Failed to execute Langfuse query:`, error);
      }
    }

    // Fall back to LangChain if all parallel attempts fail
    return this.directLLMProcessing(userQuery, options);
  }

  private async generateNLPEnhancedQuery(nlpResult: NLPResult): Promise<any> {
    const { intent, entities, keywords } = nlpResult;

    let cypher = '';
    const parameters: Record<string, any> = {};

    // Generate Cypher based on intent
    switch (intent.intent) {
      case 'list_ingredients':
        // List all ingredients in the graph
        cypher = `
          MATCH (n)
          WHERE n.type = 'ingredient'
          RETURN collect(DISTINCT {name: n.name, description: n.description})[..50] as ingredients
        `;
        break;

      case 'list_recipes':
        // List all recipes in the graph
        cypher = `
          MATCH (n)
          WHERE n.type IN ['recipe', 'dish', 'meal']
          RETURN collect(DISTINCT {name: n.name, description: n.description})[..50] as recipes
        `;
        break;

      case 'find_ingredients':
        if (entities.properNouns?.length > 0 || entities.candidates?.length > 0) {
          const entityName = entities.properNouns?.[0] || entities.candidates?.[0];
          cypher = `
            MATCH (ingredient)-[r]->(dish)
            WHERE type(r) IN ['PART_OF', 'CONTAINS']
                  AND toLower(dish.name) CONTAINS toLower($entityName)
                  AND ingredient.type = 'ingredient'
            WITH dish.name as entity,
                 collect(DISTINCT ingredient.name) as ingredients
            RETURN entity, ingredients
          `;
          parameters.entityName = entityName;
        } else {
          // If no specific entity mentioned, show all ingredients
          cypher = `
            MATCH (n)
            WHERE n.type = 'ingredient'
            RETURN collect(DISTINCT {name: n.name, description: n.description})[..50] as ingredients
          `;
        }
        break;

      case 'find_recipes':
        if (entities.properNouns?.length > 0 || entities.candidates?.length > 0) {
          const ingredientName = entities.properNouns?.[0] || entities.candidates?.[0];
          cypher = `
            MATCH (ingredient)-[r]->(recipe)
            WHERE type(r) IN ['PART_OF', 'CONTAINS']
                  AND toLower(ingredient.name) CONTAINS toLower($ingredientName)
                  AND ingredient.type = 'ingredient'
                  AND recipe.type IN ['recipe', 'dish', 'meal']
            WITH ingredient.name as searchedIngredient,
                 collect(DISTINCT recipe.name) as recipes
            RETURN searchedIngredient, recipes
          `;
          parameters.ingredientName = ingredientName;
        }
        break;

      case 'count_entities':
        if (keywords.length > 0) {
          cypher = `
            MATCH (n)
            WHERE any(keyword in $keywords WHERE 
              toLower(n.type) CONTAINS toLower(keyword) OR
              toLower(n.name) CONTAINS toLower(keyword)
            )
            RETURN n.type as type, count(n) as count
            ORDER BY count DESC
          `;
          parameters.keywords = keywords;
        }
        break;

      case 'list_entities':
        // Check if asking about nodes in general
        const queryLower = nlpResult.originalQuery.toLowerCase();
        if (queryLower.includes('node') && (queryLower.includes('have') || queryLower.includes('exist') || queryLower.includes('do you'))) {
          // Query asking about what nodes exist in the graph - return comprehensive view
          cypher = `
            MATCH (n)
            WITH n.type as type, count(*) as typeCount, collect(n)[..5] as samples
            RETURN type, typeCount, 
                   [s in samples | {name: s.name, description: s.description}] as examples
            ORDER BY typeCount DESC
          `;
        } else if ((keywords.includes('all') || keywords.includes('list')) && keywords.length > 1) {
          // Query asking for all of a specific type (e.g., "all recipes", "list ingredients")
          const typeKeyword = keywords.filter(k => k !== 'all' && k !== 'list')[0];
          if (typeKeyword) {
            cypher = `
              MATCH (n)
              WHERE toLower(n.type) = toLower($nodeType)
                    OR toLower(n.type) = toLower($nodeType) + 's'
                    OR toLower(n.type) + 's' = toLower($nodeType)
                    OR toLower(labels(n)[0]) = toLower($nodeType)
                    OR toLower(labels(n)[0]) = toLower($nodeType) + 's'
                    OR toLower(labels(n)[0]) + 's' = toLower($nodeType)
              RETURN n.type as type,
                     collect(DISTINCT {
                       name: n.name,
                       description: n.description
                     }) as entities
              LIMIT 100
            `;
            parameters.nodeType = typeKeyword;
          } else {
            // Fallback: show all nodes if no specific type
            cypher = `
              MATCH (n)
              WITH n.type as type, count(*) as typeCount, collect(n)[..5] as samples
              RETURN type, typeCount, 
                     [s in samples | {name: s.name, description: s.description}] as examples
              ORDER BY typeCount DESC
            `;
          }
        } else if (keywords.length > 0) {
          // General entity listing based on keywords
          cypher = `
            MATCH (n)
            WHERE any(keyword in $keywords WHERE 
              toLower(n.type) CONTAINS toLower(keyword) OR
              toLower(n.name) CONTAINS toLower(keyword)
            )
            RETURN n.type as type,
                   collect(DISTINCT n.name)[..20] as entities
          `;
          parameters.keywords = keywords;
        }
        break;

      default:
        // Keyword-based fallback
        if (keywords.length > 0) {
          cypher = `
            MATCH (n)
            WHERE any(keyword in $keywords WHERE 
              toLower(n.name) CONTAINS toLower(keyword) OR
              toLower(n.description) CONTAINS toLower(keyword)
            )
            RETURN n.name as name, 
                   labels(n) as labels,
                   n.type as type, 
                   n.description as description
            LIMIT 10
          `;
          parameters.keywords = keywords;
        }
    }

    if (cypher) {
      try {
        const results = await this.neo4jService.executeQuery(cypher, parameters);
        return {
          success: true,
          cypher,
          parameters,
          results
        };
      } catch (error) {
        console.error(`[QueryOrchestrator] NLP-enhanced query failed:`, error);
        return { success: false, error: error instanceof Error ? error.message : 'Unknown error' };
      }
    }

    return { success: false, error: 'Could not generate query from NLP analysis' };
  }

  private async generateFailureSuggestions(userQuery: string): Promise<string[]> {
    const suggestions: string[] = [];

    // Get available patterns from template engine
    const patterns = this.templateEngine.getAvailablePatterns();
    suggestions.push('Try queries like:');
    patterns.slice(0, 3).forEach(pattern => suggestions.push(`  â€¢ ${pattern}`));

    // Add NLP suggestions
    const nlpResult = await this.nlpProcessor.processQuery(userQuery);
    const nlpSuggestions = this.nlpProcessor.suggestQueryImprovements(nlpResult);
    suggestions.push(...nlpSuggestions);

    return suggestions;
  }

  private generateCacheKey(query: string, options: any): string {
    return `${query.toLowerCase()}:${JSON.stringify(options)}`;
  }

  private cacheResult(key: string, result: QueryResult): void {
    // Simple LRU cache
    if (this.queryCache.size >= 100) {
      const firstKey = this.queryCache.keys().next().value;
      if (firstKey) {
        this.queryCache.delete(firstKey);
      }
    }
    this.queryCache.set(key, { ...result, timestamp: Date.now() });
  }

  getMetrics() {
    const totalQueries = this.metrics.queries;
    const successByStage = Object.fromEntries(
      Object.entries(this.metrics.successByStage).map(([k, v]) => [k, v || 0])
    );
    const averageLatency = Object.fromEntries(
      Object.entries(this.metrics.totalLatency).map(([k, v]) => {
        const count = this.metrics.successByStage[parseInt(k)] || 0;
        return [k, count > 0 ? v / count : 0];
      })
    );
    const errorCounts = Object.fromEntries(
      Object.entries(this.metrics.errorsByStage).map(([k, v]) => [k, v || 0])
    );

    // Calculate overall stats
    const totalSuccess = Object.values(this.metrics.successByStage).reduce((a, b) => a + b, 0);
    const totalErrors = Object.values(this.metrics.errorsByStage).reduce((a, b) => a + b, 0);
    const successRate = totalQueries > 0 ? totalSuccess / totalQueries : 0;

    // Calculate average response time across all stages
    const totalLatency = Object.values(this.metrics.totalLatency).reduce((a, b) => a + b, 0);
    const avgResponseTime = totalSuccess > 0 ? totalLatency / totalSuccess : 0;

    // Calculate cache metrics
    const cacheHitRate = this.metrics.cacheHits > 0 ? this.metrics.cacheHits / totalQueries : 0;

    return {
      totalQueries,
      successByStage,
      averageLatency,
      errorCounts,
      // UI-friendly metrics
      successRate,
      avgResponseTime,
      cacheHitRate,
      cacheHits: this.metrics.cacheHits,
    };
  }

  clearCache(): void {
    this.queryCache.clear();
    console.log('[QueryOrchestrator] Cache cleared');
  }
}

